import{f as e,h as t,j as n,t as r}from"./chunks/plugin-vue_export-helper.Dz-VHcSB.js";const i=JSON.parse(`{"title":"AWS Serverless Application Model","description":"AWS Serverless Application Model","frontmatter":{"title":"AWS Serverless Application Model","description":"AWS Serverless Application Model","prev":{"text":"AWS","link":"./AWS"}},"headers":[],"relativePath":"Concepts/AWS/AWS_Serverless_Application_Model.md","filePath":"Concepts/AWS/AWS_Serverless_Application_Model.md","lastUpdated":1767382134000}`);var a={name:`Concepts/AWS/AWS_Serverless_Application_Model.md`};function o(r,i,a,o,s,c){return n(),e(`div`,null,[...i[0]||=[t(`<h1 id="aws-serverless-application-model" tabindex="-1">AWS Serverless Application Model <a class="header-anchor" href="#aws-serverless-application-model" aria-label="Permalink to “AWS Serverless Application Model”">​</a></h1><ul><li>SAM: Serverless Application Model</li><li>Framework for developing and deploying serverless applications</li><li>All the configuration is YAML code</li><li>Generate complex CloudFormation from simple SAM YAML file</li><li>Supports anything from CloudFormation: Outputs, Mappings, Parameters, Resources...</li><li>Only two commands to deploy to AWS</li><li>SAM can use CodeDeploy to deploy Lambda functions</li><li>SAM can help you to run Lambda, API Gateway, DynamoDB locally</li></ul><h2 id="aws-sam-recipe" tabindex="-1">AWS SAM - Recipe <a class="header-anchor" href="#aws-sam-recipe" aria-label="Permalink to “AWS SAM - Recipe”">​</a></h2><ul><li>Transform Header indicates it&#39;s SAM template: <ul><li><em>Transform: &#39;AWS::Serverless-2016-10-31&#39;</em></li></ul></li><li>Write Code: <ul><li><em>AWS::Serverless::Function</em></li><li><em>AWS::Serverless::Api</em></li><li><em>AWS::Serverless::SimpleTable</em></li></ul></li><li>Package &amp; Deploy: <ul><li><em>aws cloudformation package / sam package</em></li><li><em>aws cloudformation deploy / sam deploy</em></li></ul></li></ul><h2 id="deep-dive-into-sam-deployment" tabindex="-1">Deep dive into SAM deployment <a class="header-anchor" href="#deep-dive-into-sam-deployment" aria-label="Permalink to “Deep dive into SAM deployment”">​</a></h2><ol><li><p>aws cloudformation package:</p><ul><li>SAM Template YAML file --Transform--&gt; Generated Template CloudFormation YAML</li><li>Application Code + Swagger File (optional) --Zip and upload--&gt; Code S3 bucket --reference--&gt; Generated Template CloudFormation YAML</li></ul></li><li><p>aws cloudformation deploy:</p><ul><li>Generated Template CloudFormation YAML --Create and execute change set--&gt; AWS CloudFormation --&gt; Stack</li></ul></li></ol><ul><li><p>To installing the SAM CLI follow the steps provided in <a href="https://docs.aws.amazon.com/serverless-application-model/latest/developerguide/serverless-sam-cli-install.html" target="_blank" rel="noreferrer">AWS SAM CLI - Installation</a></p></li><li><p>See example application on how to use SAM by visiting <a href="https://github.com/amazon-archives/serverless-app-examples/tree/master/javascript" target="_blank" rel="noreferrer">AWS SAM Examples</a></p></li></ul><h2 id="sam-and-codedeploy" tabindex="-1">SAM and CodeDeploy <a class="header-anchor" href="#sam-and-codedeploy" aria-label="Permalink to “SAM and CodeDeploy”">​</a></h2><ul><li>SAM framework natively uses CodeDeploy to update Lambda functions</li><li>Traffic Shifting feature</li><li>Pre and Post traffic hooks features to validate deployment (before the traffic shift starts and after it ends)</li><li>Easy &amp; automated rollback using CloudWatch Alarms</li></ul><h2 id="sam-exam-summary" tabindex="-1">SAM - Exam Summary <a class="header-anchor" href="#sam-exam-summary" aria-label="Permalink to “SAM - Exam Summary”">​</a></h2><ul><li>SAM is built on CloudFormation</li><li>SAM requires the <strong>Transform</strong> and <strong>Resources</strong> sections</li><li>Commands to know: <ul><li>sam build: fetch dependencies and create local deployment artifacts</li><li>sam package: package and upload to Amazon S3, generate CF template</li><li>sam deploy: deploy to CloudFormation</li></ul></li><li>SAM Policy templates for easy IAM policy definition</li><li>SAM is integrated with CodeDeploy to do deploy to Lambda aliases</li></ul><h2 id="serverless-application-repository-sar" tabindex="-1">Serverless Application Repository (SAR) <a class="header-anchor" href="#serverless-application-repository-sar" aria-label="Permalink to “Serverless Application Repository (SAR)”">​</a></h2><ul><li>Managed repository for serverless applications</li><li>The applications are packaged using SAM</li><li>Build and publish applications that can be re-used by organizations</li><li>Can share publicly</li><li>Can share with specific AWS accounts</li><li>This prevents duplicate work, and just go straight to publishing</li><li>Application settings and behaviour can be customized using Environment variables</li></ul>`,13)]])}var s=r(a,[[`render`,o]]);export{i as __pageData,s as default};