import{f as e,h as t,j as n,t as r}from"./chunks/plugin-vue_export-helper.Dz-VHcSB.js";const i=JSON.parse(`{"title":"Web Development Stacks","description":"Web technology sets that include all the essential parts of a modern app - the frontend framework, the backend solution and the database","frontmatter":{"title":"Web Development Stacks","description":"Web technology sets that include all the essential parts of a modern app - the frontend framework, the backend solution and the database"},"headers":[],"relativePath":"Concepts/Web/Web_Development_Stacks.md","filePath":"Concepts/Web/Web_Development_Stacks.md","lastUpdated":1767053997000}`);var a={name:`Concepts/Web/Web_Development_Stacks.md`};function o(r,i,a,o,s,c){return n(),e(`div`,null,[...i[0]||=[t(`<h1 id="web-development-stacks" tabindex="-1">Web Development Stacks <a class="header-anchor" href="#web-development-stacks" aria-label="Permalink to “Web Development Stacks”">​</a></h1><p>A full stack web developer is a person who can develop both <strong>client</strong> and <strong>server</strong> software. So the developer has to learn tools used for both client side development and server side development</p><p>Web Development Stacks are combination of tools that are used while building a web or mobile application. There are many combination of these tools forming different stacks</p><p>The choice of tools depends on lot of variables like:</p><ul><li>Technology of the tool</li><li>Programming Language of the tool</li><li>Proficiency of the Developer in the Programming Language or the tool itself</li></ul><p>Some of the more popular Web Stacks are:</p><ol><li><strong>LAMP Stack</strong> <em>(Linux, Apache, MySQL, PHP)</em>: The Old-school Stack</li><li><strong>MEAN</strong> <em>(MongoDB, ExpressJS, Angular, NodeJS)</em>: The Jock Stack</li><li><strong>MERN</strong> <em>(MongoDB, ExpressJS, ReactJS, NodeJS)</em></li></ol>`,7)]])}var s=r(a,[[`render`,o]]);export{i as __pageData,s as default};