import{A as e,d as t,m as n}from"./chunks/runtime-core.esm-bundler.BXAIOQQS.js";import{t as r}from"./chunks/plugin-vue_export-helper.CtN8rQnz.js";const i=JSON.parse(`{"title":"The CSS","description":"","frontmatter":{},"headers":[],"relativePath":"Languages/HTML/HTML5_Boilerplate/CSS.md","filePath":"Languages/HTML/HTML5_Boilerplate/CSS.md","lastUpdated":1667218623000}`);var a={name:`Languages/HTML/HTML5_Boilerplate/CSS.md`};function o(r,i,a,o,s,c){return e(),t(`div`,null,[...i[0]||=[n(`<h1 id="the-css" tabindex="-1">The CSS <a class="header-anchor" href="#the-css" aria-label="Permalink to “The CSS”">​</a></h1><p>HTML5 Boilerplate&#39;s CSS includes:</p><ul><li><a href="#normalizecss">Normalize.css</a></li><li><a href="#stylecss">style.css</a></li></ul><p>This starting CSS does not rely on the presence of <a href="https://www.paulirish.com/2008/conditional-stylesheets-vs-css-hacks-answer-neither/" target="_blank" rel="noreferrer">conditional class names</a>, <a href="https://css-tricks.com/how-to-create-an-ie-only-stylesheet/" target="_blank" rel="noreferrer">conditional style sheets</a>, or Modernizr.js, and it is ready to use no matter what your development preferences happen to be.</p><h2 id="normalize-css" tabindex="-1">Normalize.css <a class="header-anchor" href="#normalize-css" aria-label="Permalink to “Normalize.css”">​</a></h2><p>In order to make browsers render all elements more consistently and in line with modern standards, we include Normalize.css - a modern, HTML5-ready alternative to CSS resets.</p><p>As opposed to CSS resets, Normalize.css:</p><ul><li>targets only the styles that need normalizing</li><li>preserves useful browser defaults rather than erasing them</li><li>corrects bugs and common browser inconsistencies</li><li>improves usability with subtle improvements</li><li>doesn&#39;t clutter the debugging tools</li><li>has better documentation</li></ul><p>For more information about Normalize.css, please refer to its <a href="https://necolas.github.io/normalize.css/" target="_blank" rel="noreferrer">project page</a>.</p><h2 id="style-css" tabindex="-1"><code>style.css</code> <a class="header-anchor" href="#style-css" aria-label="Permalink to “style.css”">​</a></h2><p>Several base styles are included that build upon <code>Normalize.css</code>. These styles:</p><ul><li><p>provide basic typography settings that improve text readability</p></li><li><p>protect against unwanted <code>text-shadow</code> during text highlighting</p></li><li><p>tweak the default alignment of some elements (e.g.: <code>img</code>, <code>video</code>, <code>fieldset</code>, <code>textarea</code>)</p></li><li><p>style the prompt that is displayed to users using an outdated browser</p></li></ul><p>These styles are included in <a href="https://github.com/h5bp/html5-boilerplate/blob/master/dist/css/style.css" target="_blank" rel="noreferrer"><code>style.css</code></a>. See the <a href="https://github.com/h5bp/main.css" target="_blank" rel="noreferrer"><code>style.css</code></a> project <a href="https://github.com/h5bp/main.css/blob/main/README.md#features" target="_blank" rel="noreferrer">documentation</a> for a full discussion of these styles.</p>`,13)]])}var s=r(a,[[`render`,o]]);export{i as __pageData,s as default};