import{f as e,h as t,j as n,t as r}from"./chunks/plugin-vue_export-helper.CMCX8Lry.js";const i=JSON.parse(`{"title":"EPFO","description":"Employees' Provident Fund Organisation","frontmatter":{"title":"EPFO","description":"Employees' Provident Fund Organisation"},"headers":[],"relativePath":"Non-Sorted/Finance/EPFO.md","filePath":"Non-Sorted/Finance/EPFO.md","lastUpdated":1767053997000}`);var a={name:`Non-Sorted/Finance/EPFO.md`};function o(r,i,a,o,s,c){return n(),e(`div`,null,[...i[0]||=[t(`<h1 id="employees-provident-fund-organisation-epfo" tabindex="-1">Employees&#39; Provident Fund Organisation (EPFO) <a class="header-anchor" href="#employees-provident-fund-organisation-epfo" aria-label="Permalink to “Employees&#39; Provident Fund Organisation (EPFO)”">​</a></h1><p>As per the guidelines laid out by the EPFO, every salaried professional can contribute <strong>12% of their &#39;Basic pay&#39;</strong> towards provident fund</p><p>The amount is matched by an equal deposit by the employer</p><p>Current contribution subdivision:</p><ul><li><p>Employee:</p><ul><li>12% to EPF</li></ul></li><li><p>Employer:</p><ul><li>3.67% to EPF</li><li>8.33% to EPS</li><li>0.50% to EDLI</li><li>0.65% to EPF Administrative Charges (min ₹500)</li><li>13.15% total</li></ul></li></ul><h2 id="epf-epf-and-edli" tabindex="-1">EPF, EPF and EDLI <a class="header-anchor" href="#epf-epf-and-edli" aria-label="Permalink to “EPF, EPF and EDLI”">​</a></h2><p>Employment Provident Fund and miscellaneous Act 1952:</p><p>Consists of Schemes:</p><ol><li><p>EPF (Employment Provident Fund, 1952):</p><ul><li>Retirement Savings</li></ul></li><li><p>EPS (Employment Pension Scheme, 1995):</p><ul><li><p>Post retirement pension</p></li><li><p>Capped at Rs 1250/month (i.e., 8.33% of Rs 15,000)</p></li><li><p>Get pension as:</p><ul><li><p>Commuted Pension: Pension will be received as lump-sum amount</p><ul><li>50% can be taken as lump sum (if not receiving a gratuity) else 1/3rd can be taken</li></ul></li><li><p>Un-commuted Pension: Pension will be received monthly just like salary</p><ul><li>Self: 100% until death</li><li>Spouse: 50% after death</li><li>Children (Up to Age 25): 25% each (max 2 children) after death of both parents</li></ul></li></ul></li><li><p>Pension calculation: (Pensionable salary x no. of years of service) / 70</p><ul><li>Max 35 no. of years of service</li></ul></li></ul></li><li><p>EDLI (Employment Deposit Linked Insurance Scheme, 1976):</p><ul><li>Relief to family members in case of sudden death</li></ul></li></ol><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p><strong>Higher pension scheme</strong> only for private employees who are in employment as on 1st Sep 2014</p></div><p>Info:</p><ol><li><p>Source of Contribution:</p><ul><li>Employee&#39;s Salary</li><li>Employer&#39;s Contribution</li></ul></li><li><p>Matching Deposit:</p><ul><li>Deposit made by Employee to EPF is equal to Deposit by Employer to EPF + EPS</li></ul></li><li><p>Extra amount by Employer such as EDLI &amp; Administrative charges are payed by Employer</p></li><li><p>PF Wages (Basic Pay + Dearness Allowance (DA)): PF is deducted only against PF Wages</p><ul><li><p>Basic wages</p></li><li><p>Dearness Allowances</p></li><li><p>Retaining Allowances</p></li><li><p>Conveyance Allowances</p></li><li><p>Other Allowance</p></li><li><p>Special Allowance</p></li><li><p>Leave Travel Allowances</p></li><li><p>Fixed cash Allowance (Management allowance, educational Allowance, Medical Allowance, Telephone, Food Allowance etc.)</p></li><li><p>Petrol Reimbursement (without bills and without supporting documentation/data to substantiate the reimbursement is for official purposes)</p></li><li><p>City Compensatory Allowance or any other allowance paid as fixed component, uniformly and universally having no direct nexus to the outcome of an employee&#39;s normal work</p></li></ul></li><li><p>Wage Limit: PF Wages &lt;= ₹15000</p><ul><li>An employee with a basic salary of over Rs. 15,000 and who has never been a member of EPF can opt out of the scheme. But once they become a member, they cannot opt out of the scheme</li></ul></li></ol><p>HRA, Night allowance, etc are not considered towards PF Wages</p><p>Features:</p><ul><li><p>Tax free savings, Interest @8+%</p><ul><li>Contribution + interest is Tax Free (Section 80C - Income Tax Act) If you withdraw after 5 years (maturity)</li></ul></li><li><p>Post retirement benefits:</p><ul><li>Full EPF and EPS (min service of 10 years)</li></ul></li><li><p>Emergencies (withdraw PF):</p><ul><li>Death, Marriage, House construction, Home Loan</li></ul></li><li><p>Loss of Income (withdraw PF):</p><ul><li>Jobless for 2 months and more</li></ul></li><li><p>Life Insurance:</p><ul><li>EDLI</li></ul></li><li><p>Universal Access:</p><ul><li>Universal Account Number (UAN)</li><li>Transfer EPF from old employer to new employer hassle free</li></ul></li></ul><h2 id="gratuity" tabindex="-1">Gratuity <a class="header-anchor" href="#gratuity" aria-label="Permalink to “Gratuity”">​</a></h2><ul><li>Payment of Gratuity Act, 1972</li><li>Minimum 5 years in the same Firm</li><li>Max Gratuity of 20 Lakh</li><li>Tax for calculated Gratuity, extra Gratuity paid by employer is taxable</li></ul><p>Gratuity Formula: (15 x last drawn salary x tenure of working (in years)) / 26</p><ul><li>Last drawn salary: Basic + DA</li></ul><p>In case of Death:</p><table tabindex="0"><thead><tr><th>Qualifying Service</th><th>Rate</th></tr></thead><tbody><tr><td>Less than 1 year</td><td>2 x Basic pay</td></tr><tr><td>1 or more but less than 5 years</td><td>6 x Basic pay</td></tr><tr><td>5 or more but less than 11 years</td><td>12 x Basic pay</td></tr><tr><td>11 or more but less than 20 years</td><td>20 x Basic pay</td></tr><tr><td>20+ years</td><td>Half og emoluments (salary) for every completed 6 monthly period subject to maximum of 33 times of emoluments</td></tr></tbody></table>`,21)]])}var s=r(a,[[`render`,o]]);export{i as __pageData,s as default};