import{f as e,h as t,j as n,t as r}from"./chunks/plugin-vue_export-helper.Dz-VHcSB.js";const i=JSON.parse(`{"title":"National Pension System (NPS)","description":"A government-sponsored pension scheme in India","frontmatter":{"title":"National Pension System (NPS)","description":"A government-sponsored pension scheme in India"},"headers":[],"relativePath":"Non-Sorted/Finance/NPS.md","filePath":"Non-Sorted/Finance/NPS.md","lastUpdated":1767053997000}`);var a={name:`Non-Sorted/Finance/NPS.md`};function o(r,i,a,o,s,c){return n(),e(`div`,null,[...i[0]||=[t(`<h1 id="national-pension-system-nps" tabindex="-1">National Pension System (NPS) <a class="header-anchor" href="#national-pension-system-nps" aria-label="Permalink to “National Pension System (NPS)”">​</a></h1><p>It is currently managed by 7 fund managers:</p><ul><li>LIC</li><li>HDFC Pension</li><li>UTI Mutual Fund</li><li>SBI Pension Funds</li><li>ICICI Prudential Mutual Fund</li><li>Aditya Birla Capital</li><li>Kotak Mahindra Bank</li></ul><p>Fund managers can be changed in future</p><h2 id="asset-allocation" tabindex="-1">Asset Allocation <a class="header-anchor" href="#asset-allocation" aria-label="Permalink to “Asset Allocation”">​</a></h2><p>The subscriber can choose to invest either, wholly or in combination, in 4 types of investment schemes offered by the pension fund managers. These are:</p><ul><li>Scheme E (equity) (High risk) which allows up to 75% equity participation, this is invested in stocks</li><li>Scheme C (corporate debt) (Medium risk) which invests only in high-quality corporate bonds up to 100%</li><li>Scheme G (government/gilt bonds) (Low risk) which invests only in government bonds up to 100%</li><li>Scheme A (alternative investment) (Very high risk) which allows <strong>up to 5%</strong> (newly added asset class only for private sector subscriber with active choice)</li></ul><p>Choices:</p><ol><li><p>Active Choice:</p><ul><li><p>Max equity allocation allowed is 75% till 50 years of age and reduces by 2.5% per year after that. Finally settles at 50% equity allocation when you reach 60 years of age</p></li><li><p>Percentage can be changed twice in a year</p></li></ul></li><li><p>Auto Choice:</p><ol><li><p>Aggressive life cycle fund (for High Risk Takers):</p><ul><li>Max equity allocation 75% if you&#39;re 35 or below and reduces by 4% per year till you reach 55 age (15% allocation finally)</li></ul></li><li><p>Moderate life cycle fund:</p><ul><li>Max equity allocation 50% if you&#39;re 35 or below and reduces by 2% per year till you reach 55 age (10% allocation finally)</li></ul></li><li><p>Conservative life cycle fund:</p><ul><li>Max equity allocation 25% if you&#39;re 35 or below and reduces by 1% per year till you reach 55 age (5% allocation finally)</li></ul></li></ol></li></ol><h2 id="withdrawal" tabindex="-1">Withdrawal <a class="header-anchor" href="#withdrawal" aria-label="Permalink to “Withdrawal”">​</a></h2><p>Lock-in period: 60 years of age</p><ul><li>Can be contun</li></ul><h2 id="charges" tabindex="-1">Charges <a class="header-anchor" href="#charges" aria-label="Permalink to “Charges”">​</a></h2><ul><li>A/C opening charges: Rs 500</li><li>Min transaction: Rs 500</li><li>Min contribution per year: Rs 500</li></ul>`,14)]])}var s=r(a,[[`render`,o]]);export{i as __pageData,s as default};