import{f as e,h as t,j as n,t as r}from"./chunks/plugin-vue_export-helper.Dz-VHcSB.js";const i=JSON.parse(`{"title":"Hardware Tech","description":"A list of hardware technologies used in computers, mobile devices, and other electronic devices.","frontmatter":{"title":"Hardware Tech","description":"A list of hardware technologies used in computers, mobile devices, and other electronic devices."},"headers":[],"relativePath":"Non-Sorted/Hardware_Tech.md","filePath":"Non-Sorted/Hardware_Tech.md","lastUpdated":0}`);var a={name:`Non-Sorted/Hardware_Tech.md`};function o(r,i,a,o,s,c){return n(),e(`div`,null,[...i[0]||=[t(`<h1 id="hardware-tech" tabindex="-1">Hardware Tech <a class="header-anchor" href="#hardware-tech" aria-label="Permalink to “Hardware Tech”">​</a></h1><h2 id="digital-interfaces" tabindex="-1">Digital Interfaces <a class="header-anchor" href="#digital-interfaces" aria-label="Permalink to “Digital Interfaces”">​</a></h2><h3 id="audio-and-video-interfaces" tabindex="-1">Audio and Video Interfaces <a class="header-anchor" href="#audio-and-video-interfaces" aria-label="Permalink to “Audio and Video Interfaces”">​</a></h3><ul><li><p><strong>USB (Universal Serial Bus)</strong>: A standard for connecting peripherals to computers and other devices. It supports data transfer and power supply.</p></li><li><p><strong>HDMI (High-Definition Multimedia Interface)</strong>: A standard for transmitting high-definition video and audio from a source device to a display or audio system.</p><p>Versions:</p><table tabindex="0"><thead><tr><th>Version</th><th>Resolutions</th><th>Max Color Depth</th><th>Max Bandwidth</th></tr></thead><tbody><tr><td>1.4</td><td>3840x2160@30Hz</td><td></td><td>24-bit 10.2 Gbps</td></tr><tr><td>2.0</td><td>3840x2160@60Hz</td><td>30-bit</td><td>18 Gbps</td></tr><tr><td>2.1</td><td>(8K) 7680x4320@60Hz</td><td>48-bit</td><td>48 Gbps</td></tr><tr><td></td><td>(4K) 3840x2160@120Hz</td><td></td><td></td></tr><tr><td>2.2</td><td>(8K) 7680x4320@240Hz</td><td>48-bit</td><td>48 Gbps</td></tr><tr><td></td><td>(4K) 3840x2160@480Hz</td><td></td><td></td></tr></tbody></table><ul><li>HDMI 2.2 Ultra96 Cable</li><li>HDMI Audio Return Channel (ARC) allows audio to be sent back from a display to an audio system, eliminating the need for a separate audio cable.</li></ul></li><li><p><strong>DisplayPort</strong>: A digital display interface used to connect a video source to a display device. It supports high resolutions and refresh rates.</p><ul><li>usb type-c with displayport alternate mode allows for video output over a USB-C connection, enabling the use of displayport features without needing a separate displayport connector</li><li>Daisy chaining allows multiple displays to be connected in series using a single displayport output, reducing cable clutter and simplifying multi-monitor setups</li></ul><p>Versions:</p><table tabindex="0"><thead><tr><th>Version</th><th>Resolutions</th><th>Max Color Depth</th><th>Max Bandwidth</th></tr></thead><tbody><tr><td>1.2</td><td>3840x2160@60Hz</td><td>24-bit</td><td>17.28 Gbps</td></tr><tr><td>1.4</td><td>7680x4320@120Hz</td><td>30-bit</td><td>32.4 Gbps</td></tr><tr><td>2.0</td><td>7680x4320@240Hz</td><td>30-bit</td><td>80 Gbps</td></tr><tr><td>2.1</td><td>(8K) 7680x4320@60Hz</td><td>48-bit</td><td>80 Gbps</td></tr><tr><td></td><td>(4K) 3840x2160@120Hz</td><td></td><td></td></tr></tbody></table></li><li><p><strong>Thunderbolt</strong>: A high-speed interface that combines data, video, and power in a single connection. It is commonly used for connecting external displays, storage devices, and other peripherals.</p></li><li><p><strong>VGA (Video Graphics Array)</strong>: An older analog video interface used for connecting monitors to computers. It has been largely replaced by digital interfaces like HDMI and DisplayPort.</p></li><li><p><strong>DVI (Digital Visual Interface)</strong>: A video interface that supports both digital and analog signals. It is commonly used for connecting monitors to computers.</p></li></ul><h3 id="storage-and-expansion-interfaces" tabindex="-1">Storage and Expansion Interfaces <a class="header-anchor" href="#storage-and-expansion-interfaces" aria-label="Permalink to “Storage and Expansion Interfaces”">​</a></h3><ul><li><strong>SATA (Serial ATA)</strong>: A standard for connecting storage devices like hard drives and solid-state drives to a computer&#39;s motherboard. It supports high data transfer rates and is widely used in modern computers.</li><li><strong>PCIe (Peripheral Component Interconnect Express)</strong>: A high-speed interface used for connecting expansion cards, such as graphics cards, sound cards, and network cards, to a computer&#39;s motherboard. It provides fast data transfer rates and is commonly used in gaming and professional workstations.</li><li><strong>M.2</strong>: A form factor for internal expansion cards and solid-state drives (SSDs). It is commonly used in laptops and small form factor devices due to its compact size and high performance.</li><li><strong>NVMe (Non-Volatile Memory Express)</strong>: A protocol for accessing high-speed storage devices, such as NVMe SSDs, over the PCIe interface. It provides low latency and high throughput, making it ideal for gaming, content creation, and other demanding applications.</li><li><strong>SCSI (Small Computer System Interface)</strong>: An older interface used for connecting storage devices and peripherals to computers. It has been largely replaced by newer interfaces like SATA and NVMe but is still used in some enterprise environments for high-performance storage solutions.</li><li><strong>FireWire (IEEE 1394)</strong>: A high-speed interface used for connecting digital video cameras, external hard drives, and other peripherals to computers. It has been largely replaced by USB and Thunderbolt but is still used in some professional video applications.</li><li><strong>Ethernet</strong>: A standard for wired networking that allows devices to communicate over a local area network (LAN). It supports high data transfer rates and is commonly used in homes, offices, and data centers for internet connectivity and file sharing.</li></ul>`,6)]])}var s=r(a,[[`render`,o]]);export{i as __pageData,s as default};